svg.script:
xmlns="http://www.w3.org/2000/svg"
xlink="http://www.w3.org/1999/xlink" 
Root=document.documentElement
var Nacross=12 //this is how many boxes across we'd like
var Ndown=7//this is how many we'd like vertically
function Color(){
	var R=parseInt(Math.random()*255)
	var G=parseInt(Math.random()*255)
	var B=parseInt(Math.random()*255)
	return "rgb("+R+","+G+","+B+")"
}
function startup(){
	var Stars=document.getElementsByTagName("path")
	var R=document.getElementById("R")
	var BB=R.getBBox()


	var littleBoxw=BB.width/Nacross
	var littleBoxh=BB.height/Ndown
	for (var i=0;i<Nacross;i++){
		for (var j=0;j<Ndown;j++){
			var R=document.createElementNS(xmlns,"rect")
			R.setAttribute("width",littleBoxw)
			R.setAttribute("height",littleBoxh)
			R.setAttribute("x",littleBoxw*i)
			R.setAttribute("y",littleBoxh*j)
			R.setAttribute("fill",Color())
			R.setAttribute("stroke","black")
			R.setAttribute("stroke-width",1)
			R.setAttribute("fill-opacity",.80)
			Root.appendChild(R)
			var rS=Math.floor(Stars.length*Math.random())
			var SC=Stars.item(rS)
			SBB=SC.getBBox()
			var StarClone=SC.cloneNode("false")
			var scale="scale("+littleBoxw/SBB.width+","+littleBoxh/SBB.height+") "
			var trans="translate("+(littleBoxw*i-SBB.x*(littleBoxw/SBB.width))+","+(littleBoxh*j-SBB.y*(littleBoxh/SBB.height))+") "
			StarClone.setAttribute("transform",trans+scale)
			StarClone.setAttribute("fill-rule","evenodd")
		StarClone.setAttribute("fill",Color())
			Root.appendChild(StarClone)
		}
	}
	}
